mapper.update:
    update 
    historynote 
    set note_body = #{noteBody},md_note_body = #{mdNoteBody},create_date = #{createDate},
    update_date = #{updateDate},enclosure = #{enclosure},del_flag = #{delFlag},extjson = #{extjson},label = #{label},text_type=#{textType},note_title_id=#{noteTitleId},image_context=#{imageContext},user_id=#{userId},note_title_name=#{noteTitleName},note_body_id=#{noteBodyId},last_date=#{lastDate}
    where id=#{id}

		AND user_id=#{userId}

mapper.select:
SELECT
FROM historynote hb WHERE hb.id=#{id} and hb.del_flag='N'
AND nb.user_id=#{userId}

SELECT
FROM historynote hb WHERE hb.note_body_id=#{noteBodyId} and hb.del_flag='N'
AND nb.user_id=#{userId}
